def bestdeal_sorting(data, price_range, distance_range):
    sorted_list = list()

    min_price, max_price = map(int, price_range.split("-"))
    min_dist, max_dist = map(int,  distance_range.split("-"))

    for i_hotel in data:
        if ["ratePlan", "landmarks"] in i_hotel.keys():
            i_distance, _ = i_hotel["landmarks"][0]["distance"].split(" ")
            i_price = int(i_hotel["ratePlan"]["price"]["exactCurrent"])

            if min_price <= i_price <= max_price and min_dist <= int(i_distance) <= max_dist:
                print(i_hotel["landmarks"][0]["distance"], i_hotel["ratePlan"]["price"]["exactCurrent"])
                sorted_list.append(i_hotel)
    return sorted_list


bestdeal_sorting(data=data, price_range="1000-40000", distance_range="19-25")